import React, {useState} from 'react';
import s from './GanttLightBox.module.css'

const GanttLightBox = ({task}) => {
    const [state, setState] = useState({
        name: task.text,
        start_date: task.start_date,
        end_date: task.end_date,
        description: task.description,
        checked: task.checked
    });

    function handleInputChange(event) {
        const { name, value } = event.target;
        setState(prevState => ({ ...prevState, [name]: value }));
    }

    function handleSubmit(event) {
        event.preventDefault();
        // code to handle form submission
    }

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Name:
                <input type="text" name="name" value={state.name} onChange={handleInputChange} />
            </label>
            <label>
                Start date:
                <input type="date" name="start_date" value={state.start_date} onChange={handleInputChange} />
            </label>
            <label>
                End date:
                <input type="date" name="end_date" value={state.end_date} onChange={handleInputChange} />
            </label>
            <label>
                Description:
                <textarea name="description" value={state.description} onChange={handleInputChange} />
            </label>
            <label>
                <input type="checkbox" name="checked" checked={state.checked} onChange={handleInputChange} />
                On kanban
            </label>
            <button type="submit">Submit</button>
        </form>
    );
};
export default GanttLightBox;
