import React, { Component } from 'react';
import axios from 'axios';
import { gantt } from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';

export default class Gantt extends Component {

    componentDidMount() {
        gantt.config.date_format = "%Y-%m-%d %H:%i";
        gantt.init(this.ganttContainer);
        gantt.config.columns = [
            {name:"text", label:"Задачи", width:"*", tree:true },
            {name:"add", label:"", width:44 }
        ];

        axios.get('http://localhost:8000/api/v1/gant/tasks')
            .then(response => {
                const transformedData = this.transformData(response.data);
                gantt.parse(transformedData);
            })
            .catch(error => {
                console.error(error);
            });
    }

    transformData(data) {
        return {
            data: data.map((item) => {
                return {
                    id: item.id,
                    text: item.name,
                    start_date: item.planned_start_date,
                    duration: this.calculateDuration(item.planned_start_date, item.planned_finish_date),
                    progress: item.is_completed ? 1 : 0,
                    parent: item.parent_id ? item.parent_id : 0,
                    open: true,
                }
            }),
            links: [],
        }
    }

    calculateDuration(startDate, endDate) {
        const start = new Date(startDate);
        const end = new Date(endDate);
        const diff = Math.ceil((end.getTime() - start.getTime()) / (1000 * 3600 * 24));
        return diff;
    }

    render() {
        return (
            <div
                ref={(input) => { this.ganttContainer = input }}
                style={{ width: '80%', height: '600px' }}
            ></div>
        );
    }
}
