import axios from "axios";
import {toast} from "react-toastify";
import {gantt} from "dhtmlx-gantt";

export function onKanbanViewChange(taskId, isOnKanban) {
    axios.post(`http://127.0.0.1:8000/api/v1/gant/task/${taskId}/kanban_view`, { is_on_kanban: isOnKanban })
        .then(response => {
            console.log(response.data);
            gantt.refreshRow(isOnKanban)
            if (isOnKanban === true){
                toast.success("Задача отображается на Канбане", {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "light",
                })
            }else if (isOnKanban === false){
                toast.success("Задача не отображается на Канбане", {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "light",
                })
            }
        })
        .catch(error => {
            console.error(error);
        });
}
