import React, { Component } from 'react';
import axios from 'axios';
import { gantt } from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';

export default class Gantt extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
    }

    componentDidMount() {
        gantt.config.date_format = '%Y-%m-%d %H:%i';
        gantt.init(this.ganttContainer);
        gantt.config.columns = [
            { name: 'text', label: 'Задачи', width: '*', tree: true },
            { name: 'add', label: '', width: 44 }
        ];

        axios
            .get('http://localhost:8000/api/v1/gant/tasks')
            .then(response => {
                console.log(response.data);
                const transformedData = this.transformData(response.data);
                console.log(transformedData);
                gantt.parse(transformedData);
            })
            .catch(error => {
                console.error(error);
            });
    }

    transformData(data) {
        const tasks = data.map(task => {
            const ganttTask = {
                id: task.id,
                text: task.name,
                start_date: task.planned_start_date,
                end_date: task.planned_finish_date,
                progress: 0,
                open: true,
            };

            if (task.children && task.children.length > 0) {
                ganttTask.children = this.transformData(task.children);
            }

            return ganttTask;
        });

        return tasks;
    }

    render() {
        return (
            <div
                ref={input => {
                    this.ganttContainer = input;
                }}
                style={{ width: '1200px', height: '600px' }}
            ></div>
        );
    }
}
