import React, { useState } from 'react';

const GanttLightBox = ({ onSave, onClose }) => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [deadline, setDeadline] = useState('');

    const handleSave = () => {
        const task = {
            name,
            description,
            planned_start_date: startDate,
            planned_finish_date: endDate,
            deadline,
        };

        onSave(task);
    };

    return (
        <div className="gantt-modal">
            <div className="gantt-modal-content">
        <span className="gantt-close" onClick={onClose}>
          &times;
        </span>
                <h2>Create Task</h2>
                <form>
                    <label htmlFor="task-name">Name:</label>
                    <input
                        type="text"
                        id="task-name"
                        name="task-name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />

                    <label htmlFor="task-description">Description:</label>
                    <textarea
                        id="task-description"
                        name="task-description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    ></textarea>

                    <label htmlFor="start-date">Start Date:</label>
                    <input
                        type="date"
                        id="start-date"
                        name="start-date"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                    />

                    <label htmlFor="end-date">End Date:</label>
                    <input
                        type="date"
                        id="end-date"
                        name="end-date"
                        value={endDate}
                        onChange={(e) => setEndDate(e.target.value)}
                    />

                    <label htmlFor="deadline">Deadline:</label>
                    <input
                        type="date"
                        id="deadline"
                        name="deadline"
                        value={deadline}
                        onChange={(e) => setDeadline(e.target.value)}
                    />

                    <div className="gantt-modal-buttons">
                        <button type="button" onClick={handleSave}>
                            Save
                        </button>
                        <button type="button" onClick={onClose}>
                            Cancel
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default GanttLightBox;
