import React, { useState } from 'react';
const GanttLightBox = ({taskId, name, description, is_on_kanban, is_completed, planned_start_date, planned_finish_date, deadline}) => {

    const handleNameChange = (event) => {
        const { onChange } = this.props;
        onChange("name", event.target.value);
    };

    const handleDescriptionChange = (event) => {
        const { onChange } = this.props;
        onChange("description", event.target.value);
    };

    const handleIsOnKanbanChange = (event) => {
        const { onChange } = this.props;
        onChange("is_on_kanban", event.target.checked);
    };

    const handleIsCompletedChange = (event) => {
        const { onChange } = this.props;
        onChange("is_completed", event.target.checked);
    };

    const handlePlannedStartDateChange = (event) => {
        const { onChange } = this.props;
        onChange("planned_start_date", event.target.value);
    };

    const handlePlannedFinishDateChange = (event) => {
        const { onChange } = this.props;
        onChange("planned_finish_date", event.target.value);
    };

    const handleDeadlineChange = (event) => {
        const { onChange } = this.props;
        onChange("deadline", event.target.value);
    };

    const handleSaveClick = () => {
        const { onSave } = this.props;
        onSave();
    };

    return (
        <div>
            <label>Task Name:</label>
            <input type="text" value={name} onChange={handleNameChange} />

            <label>Task Description:</label>
            <textarea value={description} onChange={handleDescriptionChange}></textarea>

            <label>Is on Kanban:</label>
            <input type="checkbox" checked={is_on_kanban} onChange={handleIsOnKanbanChange} />

            <label>Is Completed:</label>
            <input type="checkbox" checked={is_completed} onChange={handleIsCompletedChange} />

            <label>Planned Start Date:</label>
            <input type="date" value={planned_start_date} onChange={handlePlannedStartDateChange} />

            <label>Planned Finish Date:</label>
            <input type="date" value={planned_finish_date} onChange={handlePlannedFinishDateChange} />

            <label>Deadline:</label>
            <input type="date" value={deadline} onChange={handleDeadlineChange} />

            <button onClick={handleSaveClick}>Save</button>
        </div>
    );
};

export default GanttLightBox;
