import React, {Component} from 'react';
import axios from 'axios';
import './Gantt.css'
import {gantt} from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';
import s from "../Main/Main.module.css";
import CustomForm from "./GanttLightBox/CustomForm";

let toggle = false;
let count;
let taskId = null;

export default class Gantt extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
    }

    componentDidMount() {
        gantt.config.date_format = "%Y-%m-%d";

        gantt.i18n.setLocale("ru");

        gantt.config.scales = [
            {unit: "month", step: 1, format: "%F, %Y"},
            {unit: "day", step: 1, format: "%j"}
        ];

        gantt.config.scale_height = 80;

        gantt.init(this.ganttContainer);

        gantt.config.columns = [
            {name: "text", label: "ЗАДАЧИ", width: "*", tree: true},
            {name:"checked", label:"",width: "20", template:function(task) {
                    if (task.children === 0) {
                        return "<input type='checkbox' name='test' id='test' checked={task.is_on_kanban} value='1'>";
                    }
                }
            },
            {name:"add", label:"", width:44, template: function (task){
                    return "<div onclick='custom_add2("+task.id+")';>&nbsp;&nbsp;&nbsp;+&nbsp;&nbsp;</div>"} }
        ];

        // gantt.attachEvent("onBeforeLightbox", function(id) {
        //     count = 1;
        //     gantt.callEvent("onLightbox", [id]);
        //     taskId = id;
        //     let task = gantt.getTask(id);
        //
        //     let form = getForm();
        //     let input = form.querySelector("[name='description']");
        //     console.log(document.getElementsByName("description")[0]);
        //     console.log(input.value)
        //     input.focus();
        //     input.value = task.text;
        //
        //     // Adding values to date fields
        //     let start_date = form.querySelector("[name='start_date']");
        //     start_date.value = gantt.date.date_to_str("%d-%m-%Y %H:%i")(task.start_date);
        //     let duration = form.querySelector("[name='duration']");
        //     duration.value = task.duration;
        //     form.style.display = "block";
        //
        //     form.querySelector("[name='save']").onclick = save;
        //     form.querySelector("[name='close']").onclick = cancel;
        //     form.querySelector("[name='delete']").onclick = remove;
        //
        // });

        gantt.attachEvent("onBeforeLightbox", function(id) {
            count = 1;
            gantt.callEvent("onLightbox", [id]);
            taskId = id;
            let task = gantt.getTask(id);

            // create an object with the necessary task data
            let formData = {
                title: "Название задачи родителя", // replace with actual parent task title
                project: "что-то", // replace with actual project name
                deadline1: gantt.date.date_to_str("%d-%m-%Y %H:%i")(task.deadline1),
                deadline2: gantt.date.date_to_str("%d-%m-%Y %H:%i")(task.deadline2),
                date: gantt.date.date_to_str("%d-%m-%Y %H:%i")(task.date),
                description: task.text,
                performer1: "Иванов Иван Иванович", // replace with actual performer name
                performer2: "", // add more performers if necessary
                checkList: [false, false] // initialize the checklist with unchecked items
            };

            // pass the formData object as a prop to the CustomForm component
            ReactDOM.render(<CustomForm task={formData} />, document.getElementById("my_form"));

            return false; // prevent the default lightbox from opening
        });

        gantt.hideLightbox = function(){
            getForm().style.display = "none";
            taskId = null;
        }


        function plusOne(){
            let form = getForm();
            let duration = form.querySelector("[name='duration']");
            count++;
            duration.value = count;
        }

// Choosing which lightbox will be displayed
        function getForm() {
            if(!toggle)
                return document.getElementById("lightbox1");
            if (toggle)
                return document.getElementById("lightbox2");
        };


        function save() {
            let task = gantt.getTask(taskId);
            task.text = getForm().querySelector("[name='description']").value;
            // Applying dates in save function
            task.start_date = gantt.date.str_to_date("%d-%m-%Y %H:%i")(getForm().querySelector("[name='start_date']").value);
            task.end_date = gantt.date.add(new Date(task.start_date), count, 'day');



            if(task.$new){
                gantt.addTask(task,task.parent);
            }else{
                gantt.updateTask(task.id);

            }

            gantt.hideLightbox();
        }

        function cancel() {
            let task = gantt.getTask(taskId);

            if(task.$new)
                gantt.deleteTask(task.id);
            gantt.hideLightbox();
        }

        function remove() {
            gantt.deleteTask(taskId);
            gantt.hideLightbox();
        }


        gantt.config.open_tree_initially = true;

        gantt.templates.grid_file = function(obj){
            if(obj.$level === 0)
                return "<div class='gantt_tree_icon gantt_first'><i class='fas fa-plus'></i></div>";
            else
                return "<div class='gantt_tree_icon'></div>";
        };

        gantt.templates.grid_file = function (item) {
            return "<div class='gantt_tree_icon gantt_file' style='display: none'></div>";
        };

        gantt.templates.grid_folder = function (item) {
            return `<div 
   class='gantt_tree_icon gantt_folder_${(item.$open ? "open" : "closed")}' style='display: none'>
   </div>`;
        };

        gantt.templates.task_text=function(start, end, task){
            if (task.text){
             return ''
            }
        };

        gantt.templates.task_class = function (start, end, task) {
            if (task.$level === 0 || task.$level === 1 ) {
                return "parent-task";
            } else {
                return "child-task";
            }
        }

        axios.get('http://localhost:8000/api/v1/gant/tasks')
            .then(response => {
                const transformedData = this.transformData(response.data);
                gantt.parse(transformedData);

            })
            .catch(error => {
                console.error(error);
            });
    }

    createTask = (task) => {
        axios.post('/api/v1/gant/task/create', task)
            .then(response => {
                console.log('Task created:', response.data);
                gantt.addTask(task);
                gantt.hideLightbox();
            })
            .catch(error => {
                console.error('Failed to create task:', error);
            });
    };
    transformData(data) {
        const taskMap = new Map();

        function transformTask(task, parentId = 0) {
            const taskId = task.id;

            taskMap.set(taskId, {
                id: taskId,
                text: task.name,
                description: task.description,
                is_on_kanban: task.is_on_kanban,
                is_completed: task.is_completed,
                start_date: task.planned_start_date,
                end_date: task.planned_finish_date,
                deadline: task.deadline,
                open: true,
                parent: parentId,
                children: task.children.length
            });

            if (task.children) {
                task.children.forEach((child) => {
                    transformTask(child, taskId);
                });
            }
        }


        data.forEach((task) => {
            transformTask(task);
        });

        const transformedData = {
            data: Array.from(taskMap.values()),
        };

        return transformedData;
    };


