import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import axios from 'axios';

const root = ReactDOM.createRoot(document.getElementById('root'));

function render() {
    root.render(
        <React.StrictMode>
            <App />
        </React.StrictMode>
    );
}

function updateData() {
    axios.get('http://localhost:8000/api/v1/gant/tasks')
        .then(response => {
            // Обновляем данные при получении новых данных
            render();
        })
        .catch(error => {
            console.error(error);
        });
}

// Рендерим приложение сразу после загрузки страницы
render();

// Обновляем данные каждые 5 секунд
setInterval(updateData, 5000);
