import React, { Component } from 'react';
import axios from 'axios';
import { gantt } from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';

export default class Gantt extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
    }

    componentDidMount() {
        gantt.config.date_format = "%Y-%m-%d %H:%i";
        gantt.init(this.ganttContainer);
        gantt.config.columns = [
            {name:"text", label:"Задачи", width:"*", tree:true},
            {name:"add", label:"", width:44}
        ];

        axios.get('http://localhost:8000/api/v1/gant/tasks')
            .then(response => {
                const transformedData = this.transformData(response.data);
                gantt.parse(transformedData);
            })
            .catch(error => {
                console.error(error);
            });
    }

    transformData(data) {
        const transformedData = {
            data: [],
        };

        // Map through the top level tasks
        data.forEach((task) => {
            transformedData.data.push({
                id: task.id,
                text: task.name,
                start_date: task.planned_start_date,
                end_date: task.planned_finish_date,
                progress: 0,
                open: true,
                parent: task.parent_id || 0,
                children: this.getChildren(data, task.id),
            });
        });

        return transformedData;
    }

    getChildren(data, parentId) {
        const children = data.filter((task) => task.parent_id === parentId);
        return children.map((child) => ({
            id: child.id,
            text: child.name,
            start_date: child.planned_start_date,
            end_date: child.planned_finish_date,
            progress: 0,
            open: true,
            parent: child.parent_id || parentId,
            children: this.getChildren(data, child.id),
        }));
    }

    render() {
        return (
            <div
                ref={(input) => { this.ganttContainer = input }}
                style={{ width: '80%', height: '600px' }}
            ></div>
        );
    }
}
