import React, {Component} from 'react';
import axios from 'axios';
import './Gantt.css'
import {gantt} from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';
import s from "../Main/Main.module.css";
import GanttLightBox from "./GanttLightBox/GanttLightBox";

export default class Gantt extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            showModal: false
        };
        this.handleCloseModal = this.handleCloseModal.bind(this);
        this.handleSaveTask = this.handleSaveTask.bind(this);
    }

    componentDidMount() {
        gantt.config.date_format = "%Y-%m-%d";

        gantt.i18n.setLocale("ru");

        gantt.config.scales = [
            {unit: "month", step: 1, format: "%F, %Y"},
            {unit: "day", step: 1, format: "%j"}
        ];

        gantt.config.scale_height = 80;

        gantt.init(this.ganttContainer);

        gantt.config.columns = [
            {name: "text", label: "ЗАДАЧИ", width: "*", tree: true},
            {name:"checked", label:"",width: "20", template:function(task) {
                    if (task.children === 0) {
                        return "<input type='checkbox' name='test' id='test' checked={task.is_on_kanban} value='1'>";
                    }
                }
            },
            {name: "add", label: "", width: "44"},
        ];

        // gantt.config.lightbox.sections = [
        //     { name: "description", height: 38, map_to: "text", type: "textarea", focus: true },
        //     { name: "custom", height: 38, type: "template", map_to: "is_on_kanban", template: (task) => {
        //             return "<label>На канбане</label><input type='checkbox' name='is_on_kanban' " + (task.is_on_kanban ? "checked" : "") + ">"
        //         }
        //     },
        //     { name: "custom", height: 38, type: "template", map_to: "is_completed", template: (task) => {
        //             return "<label>Завершена</label><input type='checkbox' name='is_completed' " + (task.is_completed ? "checked" : "") + ">"
        //         }
        //     },
        //     { name: "time", height: 72, type: "duration", map_to: "auto" },
        //     { name: "custom", height: 38, type: "template", map_to: "start_date", template: (task) => {
        //             return "<label>Дата начала</label><input type='text' name='start_date' value='" + gantt.templates.format_date(task.start_date, "date") + "'>"
        //         }
        //     },
        //     { name: "custom", height: 38, type: "template", map_to: "end_date", template: (task) => {
        //             return "<label>Дата окончания</label><input type='text' name='end_date' value='" + gantt.templates.format_date(task.end_date, "date") + "'>"
        //         }
        //     },
        //     { name: "custom", height: 38, type: "template", map_to: "deadline", template: (task) => {
        //             return "<label>Крайний срок</label><input type='text' name='deadline' value='" + gantt.templates.format_date(task.deadline, "date") + "'>"
        //         }
        //     },
        // ];

        gantt.templates.grid_file = function(obj){
            if(obj.$level === 0)
                return "<div class='gantt_tree_icon gantt_first'><i class='fas fa-plus'></i></div>";
            else
                return "<div class='gantt_tree_icon'></div>";
        };

        gantt.templates.grid_file = function (item) {
            return "<div class='gantt_tree_icon gantt_file' style='display: none'></div>";
        };

        gantt.templates.grid_folder = function (item) {
            return `<div 
   class='gantt_tree_icon gantt_folder_${(item.$open ? "open" : "closed")}' style='display: none'>
   </div>`;
        };

        gantt.templates.task_text=function(start, end, task){
            if (task.text){
             return ''
            }
        };

        gantt.templates.task_class = function (start, end, task) {
            if (task.$level === 0 || task.$level === 1 ) {
                return "parent-task";
            } else {
                return "child-task";
            }
        }



        axios.get('http://localhost:8000/api/v1/gant/tasks')
            .then(response => {
                const transformedData = this.transformData(response.data);
                gantt.parse(transformedData);

            })
            .catch(error => {
                console.error(error);
            });
    }

    handleCloseModal() {
        this.setState({ showModal: false });
    }

    handleSaveTask(task) {
        // Save task to server
        gantt.hideLightbox();
    }

    createTask = (task) => {
        axios.post('/api/v1/gant/task/create', task)
            .then(response => {
                console.log('Task created:', response.data);
                gantt.addTask(task);
                gantt.hideLightbox();
            })
            .catch(error => {
                console.error('Failed to create task:', error);
            });
    };
    transformData(data) {
        const taskMap = new Map();

        function transformTask(task, parentId = 0) {
            const taskId = task.id;

            taskMap.set(taskId, {
                id: taskId,
                text: task.name,
                description: task.description,
                is_on_kanban: task.is_on_kanban,
                is_completed: task.is_completed,
                start_date: task.planned_start_date,
                end_date: task.planned_finish_date,
                deadline: task.deadline,
                open: true,
                parent: parentId,
                children: task.children.length
            });

            if (task.children) {
                task.children.forEach((child) => {
                    transformTask(child, taskId);
                });
            }
        }


        data.forEach((task) => {
            transformTask(task);
        });

        const transformedData = {
            data: Array.from(taskMap.values()),
        };

        return transformedData;
    };

    render() {
        return (
            <>
                <div className={s.elements}>
                    <div className={s.dropdown}>
                        <select name="tasks" id="tasks">
                            <option>Мои Задачи</option>
                        </select>
                        <select name="projects" id="projects">
                            <option>Проект 123</option>
                        </select>
                        <select name="teams" id="teams">
                            <option>Команда</option>
                        </select>
                    </div>
                    <div className={s.button}>
                        <button onClick={() => this.setState({ showModal: true })}>Создать Задачу</button>
                    </div>
                </div>
                <div
                    ref={(input) => {
                        this.ganttContainer = input
                    }}
                    style={{width: '90%', height: '85%'}}
                ></div>
                {this.state.showModal && (
                    <GanttLightBox
                        onSaveTask={this.handleSaveTask}
                        onCloseModal={this.handleCloseModal}
                    />)}
            </>
        );
    }
}
