import React from 'react';
import s from './GanttLightBox.module.css'

const GanttLightBox = ({ onClose, data }) => {
    const handleSubmit = (event) => {
        // code to handle form submission
        event.preventDefault();
        onClose();
    };

    return (
        <div className="modal">
            <div className="modal-content">
                {/* custom form elements */}
                <label htmlFor="name">Name:</label>
                <input type="text" id="name" name="name" defaultValue={data.name} />
                <label htmlFor="description">Description:</label>
                <textarea id="description" name="description" defaultValue={data.description}></textarea>

                <button type="submit" onClick={handleSubmit}>
                    Submit
                </button>
                <button type="button" onClick={onClose}>
                    Close
                </button>
            </div>
        </div>
    );
};

export default GanttLightBox;
