import React from 'react'
import s from './Main.module.css'
import Gantt from '../Gantt/Gantt'
import axios from "axios";

const Main = () => {
    const handleClick = () => {
        console.log('Кнопка нажата')
        axios.post('/api/v1/gant/task/create', {
            name: 'New Task',
            planned_start_date: new Date(),
            planned_finish_date: new Date(),
            description: '',
            is_on_kanban: false,
            parent_id: 0
        })
            .then((response) => {
                console.log(response);
            })
            .catch((error) => {
                console.error(error);
            });
    }


    return (
        <div className={s.container}>
            <div className={s.elements}>
                <div className={s.dropdown}>
                    <select name="tasks" id="tasks">
                        <option>Мои Задачи</option>
                    </select>
                    <select name="projects" id="projects">
                        <option>Проект 123</option>
                    </select>
                    <select name="teams" id="teams">
                        <option>Команда</option>
                    </select>
                </div>
                <div className={s.button}>
                    <button onClick={handleClick}>Создать Задачу</button>
                </div>
            </div>
            <div className={s.gant}>
                <Gantt/>
            </div>
        </div>
    )
}

export default Main
