import React, { useState } from 'react';
import {gantt} from 'dhtmlx-gantt';
const GanttLightBox = ({ task, save, cancel, remove }) => {
    const [text, setText] = useState(task.text);

    const handleSave = () => {
        task.text = text;

        if (task.$new) {
            delete task.$new;
            gantt.addTask(task, task.parent);
        } else {
            gantt.updateTask(task.id);
        }

        save();
    };

    const handleCancel = () => {
        if (task.$new) {
            gantt.deleteTask(task.id);
        }

        cancel();
    };

    const handleRemove = () => {
        gantt.deleteTask(task.id);
        remove();
    };

    return (
        <div id="my-form">
            <label htmlFor="description">Task text
                <input type="text" name="description" value={text} onChange={(e) => setText(e.target.value)} />
            </label>
            <br />
            <button onClick={handleSave}>Save</button>
            <button onClick={handleCancel}>Close</button>
            {!task.$new && <button onClick={handleRemove}>Delete</button>}
        </div>
    );
};

export default GanttLightBox;
