import React, { Component } from 'react';
import axios from 'axios';
import { gantt } from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';

export default class Gantt extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        }
    }

    componentDidMount() {
        gantt.config.date_format = "%Y-%m-%d %H:%i";
        gantt.init(this.ganttContainer);
        gantt.config.columns = [
            {name:"text", label:"Задачи", width:"*", tree:true},
            {name:"add", label:"", width:44}
        ];

        axios.get('http://localhost:8000/api/v1/gant/tasks')
            .then(response => {
                console.log(response.data);
                const transformedData = this.transformData(response.data);
                console.log(transformedData);
                gantt.parse(transformedData);
            })
            .catch(error => {
                console.error(error);
            });
    }

    transformData(data) {
        const transformedData = {
            data: [],
        };

        // Map through the top level tasks
        data.forEach((task) => {
            const children = task.children.map((child) => ({
                id: child.id,
                text: child.name,
                start_date: child.planned_start_date,
                duration: Math.ceil(
                    (new Date(child.planned_finish_date) - new Date(child.planned_start_date)) /
                    (1000 * 60 * 60 * 24)
                ),
                progress: 0,
            }));

            transformedData.data.push({
                id: task.id,
                text: task.name,
                start_date: task.planned_start_date,
                duration: Math.ceil(
                    (new Date(task.planned_finish_date) - new Date(task.planned_start_date)) /
                    (1000 * 60 * 60 * 24)
                ),
                progress: 0,
                open: true,
                children: children,
            });
        });

        return transformedData;
    }

    render() {
        return (
            <div
                ref={(input) => { this.ganttContainer = input }}
                style={{ width: '1200px', height: '600px' }}
            ></div>
        );
    }
}
